import React, { Component } from 'react';
import './App.css';

class App extends Component {
  render() {
    return (
      <div className="">
        <header className="">
          <h1>Current Weather</h1>
        </header>

        <div>
          <label htmlFor="zip-code">Zip Code</label>
          <input name="zip-code" onClick={this.fetchWeather} type="text" placeholder="Enter a zip code" />
        </div>
        <button>Search</button>

      </div>
    );
  }

  fetchWeather(zip) {
    const url = `api.openweathermap.org/data/2.5/weather?zip=94040,us&appid=54bf9788012da68b02084a76a5a0eaf1` 
    const request = new Request(url);
    fetch(request)
      .then(response => console.log(response.json()))
  }
}

export default App;
