import React, { Component } from 'react';

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      tempComparison: null
    }
  }

  shouldComponentUpdate(nextProps) {
    const currentCity = this.props.weatherData.name;
    const nextCity = nextProps.weatherData.name;
    return currentCity !== nextCity;
  }

  componentDidUpdate(prevProps) {
    let currentTemp = this.props.weatherData.main.temp;
    let prevTemp = prevProps.weatherData.main.temp;
    let prevCity = prevProps.weatherData.name;

    let tempComparison;
    if (prevTemp < currentTemp) {
      tempComparison = `That's warmer than ${prevCity}.`;
    } else if (prevTemp > currentTemp) {
      tempComparison = `That's colder than ${prevCity}.`;
    } else {
      tempComparison = `That's the same as ${prevCity}.`;
    }

    this.setState({
      tempComparison
    });
  }

  render() {
    const { weatherData } = this.props;
    const { tempComparison } = this.props;
    return (
     <div>
       <h2>Current Temperature for {weatherData.name}</h2>
       <p>{this.kelvinToFahrenheit(weatherData.main.temp)}</p>
       {tempComparison && <TempComparison />}
     </div> 
    );
  }

  TempComparison() {
    return (
      <p>{this.state.tempComparison}</p>
    )
  }

  kelvinToFahrenheit(kelvin) {
    return Math.floor((kelvin - 273.15) * 1.8 + 32)
  }
}

export default App;
