import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      weatherData: null
    }
  }

  componentDidMount() {
    this.getWeatherInCurrentLocation();
  }

  render() {
    console.log(this.state.weatherData)
    return (
      <div className="">
        <header className="">
          <h1>Current Weather</h1>
        </header>

      </div>
    );
  }

  getWeatherInCurrentLocation() {
    navigator.geolocation.getCurrentPosition(this.fetchWeatherByBrowserPosition);
  }

  fetchWeatherByBrowserPosition(position) {
    const { latitude, longitude } = position.coords;

    fetch(`http://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=54bf9788012da68b02084a76a5a0eaf1`)
      .then(response => response.json())
      .then(jsonData => this.setState({
        weatherData: jsonData
      }));
  }

}

export default App;
