import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      temperature: null
    }
  }

  componentDidMount() {
    this.fetchWeatherByBrowserCoordinates();
  }

  render() {
    const { temperature } = this.state; 

    return (
      <div className="">
        <header className="">
          <h1>Current Weather</h1>
        </header>

      </div>
    );
  }

  fetchWeatherByBrowserCoordinates() {
    navigator.geolocation.getCurrentPosition(position => {
      let lat = position.coords.latitude;
      let lon = position.coords.longitude;

      fetch(`http://api.openweathermap.org/data/2.5/weather?lat=${lat},lon=${lon}&appid=54bf9788012da68b02084a76a5a0eaf1`)
        .then(response => response.json())
        .then(json => console.log(json));
    });



    // fetch('http://api.openweathermap.org/data/2.5/weather?lat=${},us&appid=54bf9788012da68b02084a76a5a0eaf1')
    // .then(response => response.json())
    // .then(json => console.log(json));
  }

  async getCoordinates() {
    console.log("getting coordinates")
    return navigator.geolocation.getCurrentPosition(position => position);
  }
}

export default App;
